function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav"),s=document.querySelector(".header__section");window.scrollY>=100?(s.classList.add("body_scroll"),e.classList.add("fixed__nav")):(e.classList.remove("fixed__nav"),s.classList.remove("body_scroll"))}burgerMenu(),window.addEventListener("scroll",fixedNav);